let mobMenuIcon = document.querySelector(".mobMenuIcon"),
  mobMenu = document.querySelector(".mobNavMenu");
mobMenuIcon.addEventListener("click", function () {
  "flex" === mobMenu.style.display
    ? (mobMenu.style.display = "none")
    : (mobMenu.style.display = "flex");
}),
  document.querySelectorAll(".mobNavMenu a").forEach((e) => {
    e.addEventListener("click", function () {
      mobMenu.style.display = "none";
    });
  });
